export interface MessengerParams {
    /**
     * Позволяет выбрать вариант раскрытия мессенджера: сразу или через некоторое время.
     * Возможные значения: true - раскрыть мессенджер сразу, false - раскрыть мессенджер позже.
     * Значение по умолчанию - true.
     */
    expanded?: boolean;
    /**
     * Признак наличия звука входящих сообщений от сообществ.
     * Возможные значения: true - звук включен, false - звук выключен.
     * Значение по умолчанию - true.
     */
    messageSound?: boolean;
    /**
     * Признак наличия звука открытия мессенджера. Срабатывает при автоматическом открытии мессенджера.
     * Возможные значения: true - звук включен, false - звук выключен.
     * Значение по умолчанию - true.
     */
    openSound?: boolean;
    /**
     * Позволяет открывать мессенджер через виджет (боковую панель с аватарами пользователей), на котором отображаются сообщества с еще непрочитанными сообщениями.
     * Возможные значения: default - без аватаров, extended - с аватарами.
     * Значение по умолчанию - extended.
     */
    mode?: 'default' | 'extended';
    /**
     * Интервал до раскрытия окна мессенджера в секундах.
     * Возможные значения: 0 (при необходимости открыть мессенджер сразу), 5, 10, 15, 30, 40, 50, 60, 120, undefined (если раскрывать мессенджер не нужно)
     * Значение по умолчанию - 0.
     */
    expandTimeout?: 0 | 5 | 10 | 15 | 30 | 40 | 50 | 60 | 120 | undefined;
    /**
     * Идентификатор сообщества ВКонтакте. Принимает числовое значение меньше нуля.
     */
    peer_id: number;
    /**
     * Цветовая тема мессенджера.
     */
    scheme: 'bright_light' | 'space_gray';
}
export interface MessengerStyles {
    /**
     * Z-index мессенджера. Позволяет отображать модуль поверх других элементов на странице.
     */
    zIndex: number;
    /**
     * Положение мессенджера относительно нижнего края экрана.
     */
    bottom: string;
    /**
     * Положение мессенджера относительно правого края экрана.
     */
    right: string;
}
export interface MessengerConfig {
    /**
     * Визуальные стили мессенджера.
     */
    styles: MessengerStyles;
}
export interface MessengerInitConfig {
    /**
     * Визуальные стили мессенджера.
     */
    styles?: Partial<MessengerStyles>;
}
